mapper.select:
and tc.code like concat('%', #{code}, '%') and tc.name like concat('%', #{name}, '%') and tc.create_by like concat('%', #{createBy}, '%') and tc.update_time < #{updateTime}
and find_in_set(top.property_key,#{params.properties}) ${params.crmDataScope}
or find_in_set( #{sharedId} , tc.shared_id) or find_in_set( #{sharedDeptId} , tc.shared_dept_id)
group by tc.code

mapper.update:
update t_opportunity
code = #{code}, name = #{name}, cust_id = #{custId}, dept_id = #{deptId}, owner_id = #{ownerId}, shared_id = #{sharedId}, shared_dept_id = #{sharedDeptId}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, remark = #{remark},
where id = #{id}

mapper.select:
select id, opportunity_code, stage, advances, advances_date, creator, create_time from t_opportunity_advances
where opportunity_code = #{oppCode}
order by create_time desc,advances_date desc

select id, opportunity_code, cost, input_user_id, use_time, reason, create_time, creator_id, operator_id, update_time from t_opportunity_cost
where opportunity_code = #{oppCode}
order by create_time desc,update_time desc

select id, opportunity_code, competitor_id, competitor_name, product, unit_price, advantage, has_same_advantage, memo, tender_price, create_time, update_time from t_opportunity_competitor
 where opportunity_code = #{oppCode}
 order by create_time desc,update_time desc
